<?phpsession_start();include($_SESSION['ROOT_DIR'] . '/config.php');if (!$_SESSION['login']){  header('Location: Login.php');  return;}$panelId = mysql_real_escape_string($_POST['panelId']);if ($_REQUEST['cmd'] == 'Delete' && $panelId){  $result = mysql_query("DELETE FROM panels WHERE id = '$panelId'") or die(mysql_error());  if ($result)  {    ?>    <script  type="text/javascript">      listPanels();    </script>    <span class="Title" style="color: #339900;" >      پنل با موفقیت حذف شد    </span>            <?php  }  else  {    ?>    <span class="Title" style="color: #dd2200;" >      خطا: پنل حذف نشد    </span>    <?php  }  return;}else if ($_REQUEST['cmd'] == 'PanelsList'){  ?>  <form id="panels-list" class="form-content">    <table class="data">      <tr>        <th style="width: 50px;">          No        </th>        <th>          Name        </th>         <th>          class        </th>       </tr>      <?php      $result = mysql_query("SELECT * FROM panels") or die(mysql_error());      $rowNum = 1;      while ($row = mysql_fetch_array($result))      {        ?>		        <tr onclick="selectRow(this, <?php echo $row['id']; ?>)" ondblclick="seePanelInfo()">                                  <td>            <?php echo $rowNum++ ?>          </td>          <td>            <?php echo $row['name'] ?>          </td>          <td>            <?php echo $row['class'] ?>          </td>        </tr>        <?php      }      ?>    </table>  </form>  <?php  return;}else{  ?>      <script  type="text/javascript">      var oldRow;      var panelId;      function selectRow(rowElm, cId)      {        if (oldRow)          oldRow.className = "";        rowElm.className = "Selected";        oldRow = rowElm;        panelId = cId;        showMoreOptions(true);      }      function showTargetForm(flag)      {        if (flag)        {          obj('TargetForm').style.display = 'block';          obj('ButtonsPane').style.display = 'none';        }        else        {          obj('TargetForm').style.display = 'none';          obj('ButtonsPane').style.display = 'block';        }      }      function showMoreOptions(flag)      {        if (flag)        {          showTargetForm(false);          obj('ButtonsPane').style.visibility = 'visible';        }        else        {          obj('ButtonsPane').style.visibility = 'hidden';          if (oldRow)            oldRow.className = "";        }      }      function newPanel()      {        showTargetForm(true);        loadPage('WidgetsManagement/NEPanelForm.php', 'TargetForm', '', '<span class="CloseButton" onclick="showTargetForm(false)"></span>');      }      function seePanelInfo()      {        showTargetForm(true);        loadPage('WidgetsManagement/NEPanelForm.php', 'TargetForm', 'cmd=SeePanelInfo&panelId=' + panelId, '<span class="CloseButton" onclick="showTargetForm(false)"></span>');      }      function deletePanel()      {        if (confirm('آیا از حذف این پنل اطمینان دارید؟'))        {          showTargetForm(true);          loadPage('WidgetsManagement/PanelsList.php', 'TargetForm', 'cmd=Delete&panelId=' + panelId, '<span class="CloseButton" onclick="showTargetForm(false)"></span>');          showMoreOptions(false);        }      }      function listPanels()      {        loadPage('WidgetsManagement/PanelsList.php', 'main-content', 'cmd=PanelsList');      }      //showMoreOptions(false);      listPanels();  </script>  <?php}?>