ntById('f1_upload_process').style.visibility = 'hidden';                document.getElementById('f1_upload_form').innerHTML = result + '<span class="row">'                    +'<span class="Label">    انتخاب فایل: </span>'                    +'<input name="myfile" type="file" style="width: 280px; height: 32px;" size="30" />'                    +'</span>'                    +'<span class="row" style="text-align: center;">'                    +'<input type="submit" name="submitBtn" class="button Green" style="float: none;" value="آپلود" />'                    +'</span>';                                                                if(success != 1)                {                    path = '';                }                document.getElementById('f1_upload_form').style.visibility = 'visible';                                      callFunction = 'window.opener.'+callFunction + "('" + path + "');";                if(callFunction)                {                    var ret = eval(callFunction);                }                window.close();                return true;               }                                </script>       </head>    <body>        <div id="container" class="Panel widget" >            <div id="header_main" class="Title">                آپلودر            </div>            <form action="upload.php" method="post" enctype="multipart/form-data" target="upload_target" onsubmit="return startUpload();" >                <span class="row" id="f1_upload_process" style="visibility: hidden; text-align: center">                           <img src="loader.gif" />                </span>                <span id="f1_upload_form" >                    <span class="row">                        <span class="Label">                            انتخاب فایل:                        </span>                        <input name="myfile" id="myfile" type="file" style="width: 280px; height: