OM content_categories WHERE parent_id = '$parentId' ORDER BY content_categories.order") or die(mysql_error());            $rowNum = 1;            while ($row = mysql_fetch_array($result))            {                ?>		                <tr onclick="selectLinkRow(this,'categoryId=<?php echo $row['id']; ?>')" ondblclick="listCategories(<?php echo $row['id']; ?>)" >                    <td><?php echo $rowNum++; ?></td>                                <td><?php echo $row['title']; ?></td>                                <td><?php echo $row['order']; ?></td>                  </tr>                <?php            }            ?>        </table>    </div>    <div class="row" style="margin: 0px; margin-top: 15px;">        <label>            Articles        </label>    </div>            <div class="row" >        <table class="data">            <tr>                <th style="width: 50px;">                    No                </th>                <th style="width: 200px;">                    Title                </th>                <th>                    Order                </th>                                                    </tr>            <?php            $result = mysql_query("SELECT * FROM contents WHERE category_id = '$parentId' ORDER BY contents.order") or die(mysql_error());            $rowNum = 0;            while ($row = mysql_fetch_array($result))            {                ?>		                <tr onclick="selectLinkRow(this,'articleId=<?php echo $row['id']; ?>')" >                    <td><?php echo $rowNum++; ?></td>                                <td><?php echo $row['title']; ?></td>                                <td><?php echo $row['order']; ?></td>                          </tr>                <?php            }            ?>        </table>    </div>    <?php    return;}if ($_REQUEST['cmd'] == 'Sections'){    ?>    <div class="row" style="margin: 0px;">        <span class="ExitBtn" style="margi