<?phpsession_start();include_once $_SESSION['ROOT_DIR'] . '/config.php';$cId = $widgetParameters['categoryId'];if ($cId == '[news]'){  $cId = $_REQUEST['categoryId'];  $res = mysql_fetch_array(mysql_query("SELECT * FROM content_categories WHERE parent_id = '$cId' AND title = '[news]'"));  $cId = $res['id'];}$newsCount = $widgetParameters['newsCount'];$autoSize = $widgetParameters['autoSize'];if (!$autoSize)  $autoSize = 'false';//$title = '';$contentCategoryInfo = mysql_fetch_array(mysql_query("SELECT * FROM content_categories WHERE id = '$cId'"));if ($widgetInfo['show_title']){  ?>  <h4>    آرشیو:         <?php echo $contentCategoryInfo['title'] ?>  </h4>  <?php}?><style>  .archive  {    margin:0px;    padding:0px;    direction:rtl;    margin-right: 20px;    padding-bottom: 10px;  }  .archive li  {  }  .archive li a  {           height:20px;    line-height:20px;    text-decoration:none;    color:#000;  }  .archive li a:visited  {           color:#777;  }  .archive li a:hover  {    text-decoration:underline;  }  .archive li label  {    margin-right: 0px;    color:#aaa;    font-size:10px;  }</style><ul class="archive" >  <?php  $result = mysql_query("SELECT * FROM contents WHERE category_id = '$cId' ORDER BY date_created DESC") or die(mysql_error());  while ($row = mysql_fetch_array($result))  {    $pageTitle = $row['title'];    ?>    <li>      <a href="article/<?php echo $row['id'] ?>" class="NewsItem">             <?php echo $pageTitle ?>                     </a>       <label>        <?php echo mds_date("Y-m-d", strtotime($row['date_created']), 1); ?>         </label>    </li>    <?php  }  ?></ul>