<?phpsession_start();if (isset($_REQUEST['email']) && $widgetParameters['eMail']){  $to = $widgetParameters['eMail'];  $subject = "Contact Us";  $message = "<html><body><h2>" . $_REQUEST['name'] . "</h2><br><p style='font-family: Tahoma;'>" . $_REQUEST['message'] . "</p></body></html>";  $from = $_REQUEST['email'];  $headers = "From: " . $from . "\r\n";  $headers .= "Reply-To: " . $from . "\r\n";  //$headers .= "CC: susan@example.com\r\n";  $headers .= "MIME-Version: 1.0\r\n";  $headers .= "Content-Type: text/html; charset=UTF-8\r\n";  if (mail($to, $subject, $message, $headers))  {    ?>    <h4>      متشکریم    </h4>    <span class="Row">      <span class="Label green" >        پیام شما ارسال شد      </span>     </span>    <span class="Row">      <a class="button Blue" href="">        بازگشت      </a>    </span>    <?php    $_REQUEST['email'] = '';  }  else  {    ?>    <h4>      متاسفیم    </h4>    <span class="Row">      <span class="Label Red" >        پیام شما ارسال نشد      </span>     </span>    <span class="Row">      <a class="button Blue" href="">        بازگشت      </a>    </span>    <?php  }  return;}?><h2 class="">  GET IN TOUCH</h2><form id="ContactUsForm" action="" method="POST" class="col-xs-12">  <div class="row">    <div class="col-xs-12 mar-bot">      <label >        Name      </label>       <input type="text" class="text-field"  name="name">    </div>    <div class="col-xs-12 mar-bot">      <label>        E-Mail      </label>       <input type="text" class="text-field"  name="email">    </div>    <div class="col-xs-12 mar-bot">      <label>        Message      </label>      <textarea class="text-field" name="message" style="height: 100px;" ></textarea>    </div>    <div class="col-xs-12 mar-bot">      <button type="submit" >        Send      </button>      <button type="reset">        Clean      </button>    </div>  </div></form>