<?php//\admin\WidgetsManagement::add_html_script("", widget_script($widget_id));$feeder_name = $_REQUEST["_section_name"];$feeder_app = "admin";$priority_with_url = $widget_parameters["priority-with-url"];$language = "en";if ($_REQUEST["_language"])   $language = $_REQUEST["_language"];if ($widget_parameters['feeder']){   $feeder = json_decode($widget_parameters['feeder'], TRUE);   // load widget feeder   if ($feeder["type"] == "widget-feeder")   {      $feeder_name = $feeder["feederName"];      $feeder_app = $feeder["feederApp"];   }   // Load article   else if ($feeder["type"] == "article")   {      $feeder_name = "article";      $parameter = $feeder["id"];   }   // Load article   else if ($feeder["type"] == "folder")   {      $feeder_name = "folder";      $parameter = $feeder["id"];   }   //echo json_last_error_msg();}// if default content set to true, then read the page feeder name from the URLif ($priority_with_url == "yes" && $_REQUEST["_file"] && $_REQUEST["_section_name"]){   $is_feeder_app = EWCore::is_widget_feeder("page", "*", $_REQUEST["_section_name"]);   if ($is_feeder_app)   {      $feeder_app = $is_feeder_app;      $feeder_name = $_REQUEST["_section_name"];      // Set feeder app to *       $feeder_app = EWCore::is_widget_feeder("page", "*", $feeder_name);      $parameter = $_REQUEST["_file"];   }}if ($feeder_name && $feeder_app && EWCore::is_widget_feeder("page", $feeder_app, $feeder_name)){   // Load the page specified by URL   //$page = EWCore::get_widget_feeder("page", $feeder_app, $feeder_name, [$parameter, $language]);   $page = json_decode(EWCore::get_widget_feeder("page", $feeder_app, $feeder_name, [$parameter, $language]), true);   if ($page["html"] == "WIDGET_DATA_MODEL")   {      $pageTitle = $page['title'];      $pageContent = $page['content'];   }   else   {      echo $page["html"];      return;   }}/* if ($widget_parameters["title"] && $widget_parameters["title"] != "false")  {  echo "<{$widget_parameters["title"]} class='widget-header'>" . $pageTitle . "</{$widget_parameters["title"]}>";  } */echo $pageContent;if ($widget_parameters['linkAddress']){   ?>   <a class="SeeMore EnterIcon" href="<?php echo $widget_parameters['linkAddress'] ?>">      <?php echo $widget_parameters['linkName'] ?>   </a>   <?php}?><script>   $(document).ready(function ()   {      var animation;<?phpecho ($widget_parameters['animation']) ? "animation='{$widget_parameters['animation']}';" : "";?>      switch (animation)      {         case "1":            $("[data-widget-id={$widget_id}]").hide();            $("[data-widget-id={$widget_id}]").fadeIn(600);            break;         case "2":            $("[data-widget-id={$widget_id}]").hide();            $("[data-widget-id={$widget_id}]").animate({height: "toggle"}, 600, "Power2.easeOut");            break;      }   });</script>