_content;if ($widget_parameters['linkAddress']) {  ?>  <a class="SeeMore EnterIcon" href="<?= $widget_parameters['linkAddress'] ?>">    <?= $widget_parameters['linkName'] ?>  </a>  <?php}/*<script>   (function () {      var animation;<?phpecho ($widget_parameters['animation']) ? "animation='{$widget_parameters['animation']}';" : "";?>      switch (animation)      {         case "1":            $("[data-widget-id={$widget_id}]").hide();            $("[data-widget-id={$widget_id}]").fadeIn(600);            break;         case "2":            $("[data-widget-id={$widget_id}]").hide();            $("[data-widget-id={$widget_id}]").animate({height: "toggle"}, 600, "Power2.easeOut");            break;      }   })();</script>*/