<?phpsession_start();$widget_style_class .= "item-viewer";$feeder = json_decode($widget_parameters["feeder"], TRUE);$feeder_id = $feeder["type"];$id = $feeder["id"];if (!$feeder_id)   return;//$list = json_decode($widget_parameters["list"], TRUE);if ($widget_parameters["default-content"] == "yes"){   if ($_REQUEST["_page"])   {      $feeder_id = $_REQUEST["_page"];   }}$token = $_REQUEST["$feeder_id-token"];$num_of_items_per_page = 10;if (!$token)   $token = 0;$row_num = ($token * $num_of_items_per_page) + $num_of_items_per_page;$items_list = EWCore::get_widget_feeder("list", $feeder_id, [$id, $token * $num_of_items_per_page, $num_of_items_per_page]);$items_list = json_decode($items_list, TRUE);$items_count = $items_list["num_rows"];$items = $items_list["items"];$page = parse_url($_SERVER["REQUEST_URI"], PHP_URL_PATH);if ($items_list["header"])   echo $items_list["header"];if ($widget_parameters["show_top_buttons"]){   echo "<div class='row'><div class='col-xs-12'>";   echo ($token > 0) ? "<a href='$page?$feeder_id-token=" . ($token - 1) . " class='btn btn-link btn-perv'>{Pervious}</a>" : "";   echo ($row_num < $items_count) ? "<a href='$page?$feeder_id-token=" . ($token + 1) . " class='btn btn-link btn-perv'>tr{Next}</a>" : "";   echo "</div></div>";}?><div id="list-viewer" class="row">   <?php   foreach ($items as $item)   {      echo "<div class='item {$item["class"]}' style='{$item["style"]}'>{$item["html"]}</div>";   }   ?></div><?phpif ($widget_parameters["show_bottom_buttons"]){   echo "<div class='row'><div class='col-xs-12'>";   echo ($token > 0) ? "<a href='$page?$feeder_id-token=" . ($token - 1) . " class='btn btn-link btn-perv'>{Pervious}</a>" : "";   echo ($row_num < $items_count) ? "<a href='$page?$feeder_id-token=" . ($token + 1) . " class='btn btn-link btn-perv'>tr{Next}</a>" : "";   echo "</div></div>";}