<?phpsession_start();include_once $_SESSION['ROOT_DIR'] . '/config.php';$cId = $widgetParameters['categoryId'];$newsCount = $widgetParameters['newsCount'];$title = '';$contentCategoryInfo = mysql_fetch_array(mysql_query("SELECT * FROM content_categories WHERE id = '$cId'")) or die(mysql_error());$title = $contentCategoryInfo['title'];if ($widgetInfo['show_title']){    ?>    <h4>        <?php echo $title; ?>    </h4>    <?php}?><span class="content">    <?php    $result = mysql_query("SELECT * FROM contents WHERE category_id = '$cId' ORDER BY date_created DESC LIMIT $newsCount") or die(mysql_error());    while ($row = mysql_fetch_array($result))    {        $pageTitle = $row['title'];        ?>        <span class="NewsItem">                 <?php echo $pageTitle ?>            <a href="index.php?articleId=<?php echo $row['id'] ?>">                ادامه            </a>            <label>                <?php                $createdDate = mds_date("Y-m-d", strtotime($row['date_created']), 1);                echo $createdDate;                ?>            </label>        </span>        <?php    }    ?></span>      