<?phpsession_start();global $EW;EWCore::deregister("uis-widget-form", "size-layout");function get_content(){  ob_start();  ?>  <div id="row-separator-form">    <div class="col-xs-12">      <label for="path">        This widget create invisible separator inside the panel.<br>        You can choose in which screen this separator affect the content      </label>    </div>    <div class="col-xs-12 margin-bottom" >      <label class="aln-center" >        Hide in      </label>      <div class="btn-group btn-group-justified" data-toggle="buttons">        <input type="checkbox" name="hidden-on" id="hidden-on" value="row-separator" checked="true" style="display:none;">        <label class="btn btn-default ">          <input type="checkbox" name="hidden-on" id="hidden-on" value="hidden-xs" > Mobile Screen        </label>        <label class="btn btn-default ">          <input type="checkbox" name="hidden-on" id="hidden-on" value="hidden-sm" > Tablet Screen        </label>        <label class="btn btn-default ">          <input type="checkbox" name="hidden-on" id="hidden-on" value="hidden-md" > Normal Screen        </label>        <label class="btn btn-default ">          <input type="checkbox" name="hidden-on" id="hidden-on" value="hidden-lg" > Large Screen        </label>      </div>    </div>  </div>  <?php  return ob_get_clean();}function get_script(){  ob_start();  ?>  <script  type="text/javascript">    var classes = $("#used-classes").text();    classes = classes.split(" ");    $.each($("#row-separator-form").find("input:checkbox"), function(k, v) {      $.each(classes, function(i, c) {        if ($(v).val() === c && !$(v).is(":checked"))        {          $(v).click();          $(v).prop("checked", true);        }      });    });    $("#row-separator-form input:checkbox").change(function(event) {      uisWidget.setClasses();    });    uisWidget.setClasses();  </script>  <?php  return ob_get_clean();}?>