<?php//\admin\WidgetsManagement::add_html_script("", widget_script($widget_id));$format = $widget_parameters["format"];if ($widget_parameters['feeder']) {  $feeder = json_decode($widget_parameters['feeder'], TRUE);  $content_id = $feeder["id"];  $field_id = $feeder["fieldId"];}else {  echo "<$format>&nbsp;</$format>";  return;}$language = "en";if ($_REQUEST["_language"]) {  $language = $_REQUEST["_language"];}//$contentFields = json_decode(EWCore::call("admin/api/content-management/content-fields", ["id" => $content_id]), true);$content = EWCore::call_cached_api("admin/api/content-management/contents-labels", [            "content_id" => $content_id,            "key"        => "admin_ContentManagement_language",            "value"      => $language        ])["data"];if (isset($content)) {  $contentFields = $content[0]["content_fields"];  echo "<$format> {$contentFields[$field_id]["content"]} </$format>";}else {  echo "<span style='color:red'>Text not found</span>";}